import React from "react";
import TecladoButton from "./TecladoButton";

function Teclado({ teclas, setSenha, senha }) {
  const handleClick = (value) => {
    setSenha(senha + value); // ⬅ Atualiza o estado da senha diretamente no App.js
  };

  return (
    <div className="teclado-container">
      <div className="display">
        <input
          type="password"
          value={senha} // ⬅ Exibe o valor vindo do App.js
          readOnly
          className="display-input"
          placeholder="Digite os números"
        />
      </div>
      <div className="teclado">
        {teclas.map((tecla, index) => (
          <TecladoButton
            key={index}
            label={tecla.label}
            value={tecla.value}
            onClick={() => handleClick(tecla.value)} // ⬅ Passa o valor ao clicar
          />
        ))}
      </div>
    </div>
  );
}

export default Teclado;
