import React, { useEffect, useState } from "react";
import Teclado from "./components/Teclado";
import AcessButton from "./components/AcessButton";
import tecladoService from "./services/tecladoService";
import logo from "../src/resources/catolica.png";

function App() {
  const [teclado, setTeclado] = useState([]);
  const [senhaDigitada, setSenhaDigitada] = useState(""); // Estado para armazenar a senha
  const [erro, setErro] = useState("");

  useEffect(() => {
    gerarTeclado();
  }, []);

  const gerarTeclado = async () => {
    try {
      const response = await tecladoService.gerarTeclado();
      setTeclado(response.data);
    } catch (err) {
      setErro("Erro ao carregar teclado");
    }
  };

  const handleTeclaPressionada = (valor) => {
    setSenhaDigitada((prev) => prev + valor); // Concatena os números pressionados
  };

  const acessarSistema = async () => {
    try {
      console.log("Senha sendo enviada: ", senhaDigitada);
      await tecladoService.acessarSistema(senhaDigitada);
      setErro("");
      setSenhaDigitada(""); // Limpa a senha após o envio
      gerarTeclado();
    } catch (err) {
      setErro("Erro ao acessar sistema");
    }
  };

  return (
    <div className="App">
      <h1>Teclado Virtual</h1>
      {erro && <p className="error-message">{erro}</p>}
      <Teclado teclas={teclado} onClick={handleTeclaPressionada} />
      <AcessButton onAcess={acessarSistema} />

      <img src={logo} alt="Católica SC" className="logo" />
    </div>
  );
}

export default App;
