import React, { useState } from 'react';
import Modal from 'react-modal';
import axios from 'axios';

Modal.setAppElement('#root');

function CreateUserModal({ isOpen, onRequestClose }) {
  const [nomeUsuario, setNomeUsuario] = useState('');
  const [senha, setSenha] = useState('');
  const [email, setEmail] = useState('');
  const [erro, setErro] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:3001/usuarios', {
        nome_usuario: nomeUsuario,
        senha_hash: senha,
        email: email,
      });
      onRequestClose();
      setNomeUsuario('');
      setSenha('');
      setEmail('');
      setErro('');
    } catch (error) {
      setErro('Erro ao criar usu치rio');
    }
  };

  return (
    <Modal
      isOpen={isOpen}
      onRequestClose={onRequestClose}
      className="custom-modal-content"
      overlayClassName="custom-modal-overlay"
    >
      <h2>Criar Usu치rio</h2>
      {erro && <p className="error-message">{erro}</p>}
      <form onSubmit={handleSubmit}>
        <div>
          <label>Nome de Usu치rio:</label>
          <input
            type="text"
            value={nomeUsuario}
            onChange={(e) => setNomeUsuario(e.target.value)}
            required
            className="input-field"
          />
        </div>
        <div>
          <label>Senha:</label>
          <input
            type="password"
            value={senha}
            onChange={(e) => setSenha(e.target.value)}
            required
            className="input-field"
          />
        </div>
        <div>
          <label>Email:</label>
          <input
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
            className="input-field"
          />
        </div>
        <button type="submit" className="modal-button create">
          Criar
        </button>
        <button
          type="button"
          onClick={onRequestClose}
          className="modal-button cancel"
        >
          Cancelar
        </button>
      </form>
    </Modal>
  );
}

export default CreateUserModal;
