import axios from "axios";

const API_URL = "http://localhost:3000/api"; 

const gerarTeclado = async () => {
  return axios.get(`${API_URL}/teclado`);
};

// const acessarSistema = async (senha) => {
//   return axios.post(`${API_URL}/teclado/acessar`, { senha });
// };

const acessarSistema = async (senha) => {
  try {
    // Passando a senha corretamente no corpo da requisição
    const response = await axios.post(`${API_URL}/teclado/acessar`, { senha });

    // Retorna a resposta do servidor, caso a requisição tenha sucesso
    return response.data;
  } catch (error) {
    console.error("Erro ao acessar o sistema:", error);
    throw error;
  }
};


export default { gerarTeclado, acessarSistema };
