import React from "react";
import TecladoButton from "./TecladoButton";

function Teclado({ teclas, setSenha, resetSenha }) {
  const handleClick = (value) => {
    setSenha((prevSenha) => prevSenha + value);
  };

  const handleApagar = () => {
    setSenha((prevSenha) => prevSenha.slice(0, -1)); // Remove o último caractere
  };

  return (
    <div className="teclado-container">
      <div className="teclas">
        {teclas.map((tecla, index) => (
          <TecladoButton key={index} label={tecla} value={tecla} onClick={handleClick} />
        ))}
      </div>
      <button className="apagar-button" onClick={handleApagar}>
        Apagar
      </button>
    </div>
  );
}

export default Teclado;
